package com.onebyte.tagmoo.utils;import android.app.Dialog;import android.content.Context;import android.util.Log;import android.view.Gravity;import android.view.WindowManager;import com.onebyte.tagmoo.R;public class ProgressHUD extends Dialog {    public ProgressHUD(Context context) {        super(context);    }    public ProgressHUD(Context context, int theme) {        super(context, theme);    }    public static ProgressHUD showLoadingDialog(Context context) {        ProgressHUD dialog = new ProgressHUD(context, R.style.ProgressHUD);        dialog.setTitle("");        dialog.setContentView(R.layout.progress_hud );        dialog.getWindow().setLayout(WindowManager.LayoutParams.MATCH_PARENT,                WindowManager.LayoutParams.MATCH_PARENT);        dialog.setCancelable(false);        dialog.getWindow().getAttributes().gravity= Gravity.CENTER;        WindowManager.LayoutParams lp = dialog.getWindow().getAttributes();        lp.dimAmount=0.2f;        dialog.getWindow().setAttributes(lp);        dialog.getWindow().addFlags(WindowManager.LayoutParams.FLAG_BLUR_BEHIND);        try {            dialog.show();        } catch (Exception ex){            Log.e("DIALOG_ERROR", ex.toString());        }        return dialog;    }}